import React from 'react';
import Header from './Header';
import Order from './Order';
import Inventory from './Inventory';
import sampleFishes from '../sample-fishes';
import Fish from './Fish';
import base from '../base'


class App extends React.Component {
  state = {
    fishes: {}, 
    order: {}
  };

  componentDidMount() {
    const { params } = this.props.match; 
    // first reinstate local storage
    const localStorageRef = localStorage.getItem(params.storeId)
    if(localStorageRef) {
      this.setState({ order: JSON.parse(localStorageRef)}) // string to object
    }
    this.ref = base.syncState(`${params.storeId}/fishes`, {
      context: this, 
      state: "fishes"
    })
  }

  componentDidUpdate() {
    console.log(this.state.order)
    localStorage.setItem(this.props.match.params.storeId, JSON.stringify(this.state.order)) // object
  }

  componentWillUnmount() {
    base.removeBinding(this.ref);
  }


  addFish = fish => {
    // take a copy of existing state
    const fishes = {...this.state.fishes};
    // add our new fish to that fishes variable
    fishes[`fish${Date.now()}`]= fish;
    // Set the new fishes object to state
    this.setState({ fishes });
  };

  updateFish = (key, updatedFish) => {
    // 1. Take a copy of the current state
    const fishes = { ... this.state.fishes};
    // 2. update that state
    fishes[key] = updatedFish;
    // 3. Set that to state
    this.setState ({ fishes })
  };

  loadSampleFishes = () => {
    this.setState({ fishes: sampleFishes })
  };

  addToOrder = (key) => {
    // take a copy of state
    const order = {...this.state.order};
    // add to the order or update the number to our order
    order[key] = order[key] + 1 || 1;
    // call set state to update our state
    this.setState({ order })
  };

  render() {
    return (
      <div className="catch-of-the-day">
        <div className="menu">
          <Header tagline="Fresh Seafood 24/7"/>
          <ul className="fishes">
            {Object.keys(this.state.fishes).map(key => (
              <Fish 
                key={key} 
                index = {key}
                details={this.state.fishes[key]} 
                addToOrder={this.addToOrder}
                /> 
              ))}
          </ul>
          </div>
        <Order fishes={this.state.fishes} order={this.state.order} />
        <Inventory 
          addFish={this.addFish} 
          updateFish = {this.updateFish}
          loadSampleFishes={this.loadSampleFishes}
          fishes= {this.state.fishes}
          />
      </div>
      );
  }
}

export default App;
